import React, { useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import  { useDispatch} from 'react-redux';

import { getPosts } from './actions/posts';
import Posts from './components/Posts/Posts';
import Form from './components/Form/Form';
import blogger from './images/blogger.png';
import useStyles from './styles';



const App =() => {
    const classes =useStyles();
    const dispatch = useDispatch();
    
    useEffect(() => {
        dispatch(getPosts());
    }, [dispatch]);
    
    return(
        <Container maxidth="lg">
            <AppBar className= {classes.appBar} postition ="static" color="inherit">
                <Typography className={classes.heading} variant="h2" align="center">Blogger</Typography>
                <img className={classes.image} src={blogger} alt="blogger" align="center" height="60" /><br></br><br></br>
            </AppBar>
            <Grow in>
                <Container>
                    <Grid container justify="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts />
                        </Grid>
                        <Grid item xs={12} sm={4}>
                            <Form />
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
        </Container>
    );
}
export default App;
