import React, {useState} from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';

import useStyles from './styles';
import { createPost } from '../../actions/posts';
const Form = () => {
    const [postData, setPostData]= useState({ writer:'', title: '', blog: '', tags:'', selectedFile: ''});
    const classes =useStyles();

    const dispatch = useDispatch();
    const handleSubmit =(e) => {
        e.preventDefault();

        dispatch (createPost(postData));
    }
    const clear =() => {

    }
    return (
        
        <Paper className={classes.paper}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={ handleSubmit }>
        <Typography variant="h6">  Write Here a BlogPost</Typography>
        <TextField name="creator" variant="outlined" label="Writer" fullWidth value={postData.writer} onChange={(e) => setPostData({ ...postData, writer: e.target.value })} />
        <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
        <TextField name="blog" variant="outlined" label="Blog" fullWidth multiline rows={4} value={postData.blog} onChange={(e) => setPostData({ ...postData, blog: e.target.value })} />
        <TextField name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
        <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
        <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
        <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
      </form>
    </Paper>
    );

}
export default Form;
